import React from 'react';
import './MenuOption.css';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

const APIMenu = ({isVisible}) => {

    const data = [
        { description: "Uploads a package", method: 'POST', path: '/upload/package-name' },
        { description: "Checks whether a package is exists on Nexus", method: 'GET', path: '/check/package-name' },
    ];

    if(!isVisible){
        return null;
    }

    return(
        <React.Fragment>
            <ListGroup className="my-2">
            {data.map((item => 
                <ListGroup.Item>
                    <h6>{item.method}</h6>
                    <h1>{item.description}</h1>
                    <h3>{item.path}</h3>
                </ListGroup.Item>
            ))}
            </ListGroup>
            {data.map((item => 
                <Card body>
                    <h6>{item.method}</h6>
                    <h1>{item.description}</h1>
                    <h3>{item.path}</h3>
                </Card>
            ))}
        </React.Fragment>
        // <div style={{ padding: '10px'}}>
        //     <Button variant="dark" size="lg" block style={{ padding: '10px'}}>
        //         REST API
        //     </Button>
        // </div>

    );
}

export default APIMenu;

// {props.action} is exisits and supposed to be athe actionKey.
