import React from 'react';
import './MenuOption.css';
import Button from 'react-bootstrap/Button';
import Accordion from 'react-bootstrap/Accordion';
import Form from 'react-bootstrap/Form'

function MenuOption(props) {

    let uploadComponnent

    if(props.value == "Upload Packages") {
        uploadComponnent =  <Accordion.Collapse eventKey="0">
                                <Form>
                                    <Form.File 
                                        id="packages"
                                        label="Select Packages..."
                                        custom
                                    />
                                </Form>
                            </Accordion.Collapse>;
    } else {
        uploadComponnent =  <Accordion.Collapse eventKey="">
                                <Form>
                                    <Form.File 
                                        id="packages"
                                        label="Select Packages..."
                                        custom
                                    />
                                </Form>
                            </Accordion.Collapse>;
    }

    return(
        <div className="menu-option" style={{ padding: '10px'}}>
            <Accordion.Toggle as={Button} variant="dark" size="lg" block eventKey="0">
                {props.value}
            </Accordion.Toggle>
            {uploadComponnent}
        </div>
    );
}

export default MenuOption;

// {props.action} is exisits and supposed to be athe actionKey.
