import React from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form'
import Accordion from 'react-bootstrap/Accordion';

class UploadMenu extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            uploadMenuState: [
                { color: 'dark', text: 'Upload Packages' },
                { color: 'light', text: 'Click Here To Upload' },
                { color: 'success', text: 'Finished !' },
            ],
            uploadMenuStateCode: 0,
        }
    }

    handleClick() {
        if (this.state.uploadMenuStateCode == 0) {
            console.log('onFormSubmit : ', this.state); 
            this.setState({
                uploadMenuStateCode: 1
            });
        } else if (this.state.uploadMenuStateCode == 1) {
            this.setState({
                uploadMenuStateCode: 2
            });
        }
    }

    render() {
    return(
        <Accordion className="upload-menu" style={{ padding: '10px'}}>
            <Accordion.Toggle as={Button} size="lg" block eventKey="0" variant={this.state.uploadMenuState[this.state.uploadMenuStateCode].color} onClick={this.handleClick}>
                {/* {this.state.uploadMenuState[this.state.uploadMenuStateCode].text} */}
                {this.state.uploadMenuStateCode}
            </Accordion.Toggle>
            <Accordion.Collapse eventKey="0">
                <Form fluid>
                    <Form.File 
                        id="packages"
                        label="Select Packages..."
                        custom
                    />
                </Form>
            </Accordion.Collapse>
        </Accordion>
    );
    }
}

export default UploadMenu;
