import React from 'react';
import './MenuOption.css';
import ListGroup from 'react-bootstrap/ListGroup';
import Card from 'react-bootstrap/Card';

const APIMenu = ({isVisible}) => {

    const data = [
        { description: "Uploads a package", method: 'POST', path: '/upload/package-name' },
        { description: "Checks whether a package is exists on Nexus", method: 'GET', path: '/check/package-name' },
    ];

    if(!isVisible){
        return null;
    }

    return(
        <React.Fragment>
            <Container fluid>
                <Row className="justify-content-md-left" style={{ 'padding': '40px'}}>
                    <MainNavbar/>
                </Row>
                {data.map((item => 
                    <Card style={ { 'padding': '20px', 'background-color': '#229954' }}>
                        <h2>{item.method}</h2>  
                        <h6>{item.description}</h6>    
                        <h4>{item.path}</h4>
                    </Card>
                ))}
            </Container>
        </React.Fragment>
        // <div style={{ padding: '10px'}}>
        //     <Button variant="dark" size="lg" block style={{ padding: '10px'}}>
        //         REST API
        //     </Button>
        // </div>

    );
}

export default APIMenu;

// {props.action} is exisits and supposed to be athe actionKey.
