import React from 'react';
import './Dashboard.css';
import MainNavbar from './MainNavbar';
import UploadMenu from './UploadMenu';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import APIMenu from './APIMenu';
import UploadStatusPie from './UploadStatusPie';
import { ResponsiveContainer } from 'recharts';

class Dashboard extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isAPIMenuVisible: true,
            isUploadMenuVisible: true,
            isFileUploadVisible: false,
        }
    }

    render() {
        return(
            <div className="dashboard">
                <MainNavbar></MainNavbar>
                <Container>
                    <Row>
                        <Col sm={4}></Col>
                        <Col>
                            <UploadMenu 
                                isVisible={this.state.isUploadMenuVisible}
                                isFileUploadVisible={this.state.isFileUploadVisible}
                            />
                            <APIMenu isVisible={this.state.isAPIMenuVisible}/>
                        </Col>
                        <Col sm={4}></Col>
                    </Row>
                    <Row>
                        <Col xs={4} sm={4} md={4}>
                            <ResponsiveContainer height={400} width="100%">
                            <Pie
          activeIndex={this.state.activeIndex}
          activeShape={renderActiveShape}
          data={data}
          innerRadius={30}
          outerRadius={50}
          startAngle={90} 
          endAngle={450}
          dataKey="value"
          onMouseEnter={this.onPieEnter}
          label
        >
        {
                data.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={colors[index]}/>
                ))
        }
        </Pie>
                            </ResponsiveContainer>
                        </Col>
                        <Col sm={4}></Col>
                        <Col sm={4}></Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default Dashboard;
