import React from 'react';
import logo from './logo.svg';
import './Menu.css';
import MenuOption from './MenuOption';

class Menu extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            menuOptions: ["Upload Packages", "REAST API"],
        };
    }

    renderOptions(options) {
        for(const option in options) {
            return(
                <MenuOption
                    value={option}>
                   // onClick={i => this.handleClick(i)}>
                </MenuOption>
            );
        }
    }

    render() {
        const currenOptions = this.state.menuOptions;

        return(
            <div className="menu">
                {this.renderOptions(currenOptions)}
            </div>
        );
    }
}

export default Menu;
