import React from 'react';
import logo from './logo.svg';
import './MenuOption.css';
import Button from 'react-bootstrap/Button';
import Accordion from 'react-bootstrap/Accordion';
import Form from 'react-bootstrap/Form'

function MenuOption(props) {
    return(
        //<Button className="menu-option" variant="dark" size="lg" block>{props.value}</Button>
        <div className="menu-option" padding="200px">
            <Accordion.Toggle as={Button} variant="dark" size="lg" block eventKey={props.action}>
                {props.value}
            </Accordion.Toggle>
            {props.value == "Upload Packages" &&
                <Accordion.Collapse eventKey="0">
                    <Form>
                        <Form.File 
                            id="packages"
                            label="Select Packages..."
                            custom
                        />
                    </Form>
                </Accordion.Collapse>
            }
        </div>
    );
}

export default MenuOption;
