import React from 'react';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';

function StatusCard(props) {

    const data = [
        { name: 'nodejs.12.4.rpm', value: "Success" },
        { name: 'git.1.5.3.rpm', value: "Success" },
        { name: 'java_1.8.0.el7.rpm', value: "Failed" },
        { name: 'reacharts.tar.gz', value: "Success" },
        { name: 'NexusUploaderShow.pptx', value: "Unknown" },
    ];

    return(
        <Container style={{ padding: '10%'}}>
            <Row className="justify-content-md-center">
                <Col>
                <Table striped bordered hover responsive>
                    <thead style={{ "background-color": "#424242", "color": "white", "margin": "center" }}>
                        <tr>
                            <th>Packages Status</th>
                        </tr>
                    </thead>
                    <ListGroup variant="light">
                        {data.map((item) => 
                            <ListGroup.Item> {item.name}:   {item.value}</ListGroup.Item>
                        )}
                    </ListGroup>
                </Table>
                </Col>
            </Row>
        </Container>
    );
}

export default StatusCard;
