import React from 'react';
import logo from './logo.svg';
import './Menu.css';
import MenuOption from './MenuOption';

class Menu extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            menuOptions: ["Upload Packages", "REAST API"],
        };
    }

    renderOptions(options) {
        return(
            <MenuOption
                value={options}>
            </MenuOption>
        );
    }

    render() {
        const currentOptions = this.state.menuOptions;

        return(
            <div className="menu">
                {this.renderOptions(currentOptions)}
            </div>
        );
    }
}

export default Menu;
