import React from 'react';
import Accordion from 'react-bootstrap/Accordion'

class UploadMenu extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            menuOptions: ["Upload Packages", "REST API"],
            isVisible: true,
        };
    }

    render() {

        if(this.state.isVisible) {
            return null;
        }

        return(
            <Accordion className="menu" style={{ padding: '10px'}}> 
                <Accordion.Toggle as={Button} variant="dark" size="lg" block eventKey="0">
                    Upload Packages
                </Accordion.Toggle>
                <Accordion.Collapse eventKey="0">
                    <Form>
                        <Form.File 
                            id="packages"
                            label="Select Packages..."
                            custom
                        />
                    </Form>
                </Accordion.Collapse>
            </Accordion>
        );
    }
}

export default Menu;
