import React from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form'
import Accordion from 'react-bootstrap/Accordion';

const UploadMenu = () => {

    // These are the states allowed to the upload menu to be in.
    const states = [
        { color: 'dark', text: 'Upload Packages' },
        { color: 'light', text: 'Click Here To Upload' },
        { color: 'success', text: 'Finished !' },
    ];

    // This is the state code, it's an index from the states array.
    const [stateCode, setStateCode] = React.useState(0);

    function changeMenuState() {
        if(stateCode < 2) {
            setStateCode(stateCode + 1);
        }
    }

    return(
        <Accordion className="upload-menu" style={{ padding: '10px'}}>
            <Accordion.Toggle as={Button} size="lg" block eventKey="0" 
                variant={states[stateCode].color} 
                onClick={changeMenuState()}>
                {states[stateCode].text}
            </Accordion.Toggle>
            <Accordion.Collapse eventKey="0">
                <Form fluid>
                    <Form.File 
                        id="packages"
                        label="Select Packages..."
                        custom
                    />
                </Form>
            </Accordion.Collapse>
        </Accordion>
    );
}

export default UploadMenu;
