import React from 'react';
import logo from './logo.svg';
import './Menu.css';
import MenuOption from './MenuOption';

class Menu extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            menuOptions: ["Upload Packages", "REAST API"],
        };
    }

    renderMenuOption(option){
        return(
            <MenuOption
                value={option}>
            </MenuOption>
        );
    }

    render() {
        const currentOptions = this.state.menuOptions;

        var menuOptions = new Array();

        for(const option of currentOptions) {
            menuOptions.push(
                this.renderMenuOption(option)
            );
        }

        return(
            <div className="menu">
                {currentOptions.map(item => (
                    <MenuOption
                        value={option}/>
                ))}
            </div>
        );
    }
}

export default Menu;
