import React from 'react';
import './MenuOption.css';
import Button from 'react-bootstrap/Button';

class APIOption extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            menuOptions: ["Upload Packages", "REST API"],
            isVisible: true,
        };
    }

    render(){
        return(
            <Button className="menu-option" variant="dark" size="lg" block style={{ padding: '10px'}}>
                REST API
            </Button>
        );
    }
}

export default APIOption;

