import React from 'react';
import logo from './logo.svg';
import './Menu.css';
import MenuOption from './MenuOption';

class Menu extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            menuOptions: ["Upload Packages", "REAST API"],
        };
    }

    renderMenuOption(option){
        return(
            <MenuOption
                value={option}>
            </MenuOption>
        );
    }

    render() {
        const currentOptions = this.state.menuOptions;

        return(
            <ui className="menu">
                {currentOptions.map(item => (
                    <div>
                        <MenuOption value={item}/>
                    </div>
                ))}
            </ui>
        );
    }
}

export default Menu;
